import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';

const Portfolio = props => {
    const [amountToBuy,setAmountToBuy] = useState(0);
    const maxQuantity = Math.floor(props.userBalance / props.price);

    const buyLess = () => {
        let amount = amountToBuy;
        setAmountToBuy(--amount);
    }

    const buyMore = () => {
        let amount = amountToBuy;
        setAmountToBuy(++amount);
    }

    const handleDisplay = () => {
        if(props.searchMode){
            return (
                <table className="table table-bordered">
                    <caption className="text-center">{props.stockName}</caption>
                    <tbody>
                        <tr>
                            <td>Price</td>
                            <td>{props.price}</td>
                        </tr>
                        <tr>
                            <td>How many to buy?</td>
                            <td>
                                <Button onClick={e => buyLess()}>-</Button>
                                <input min="0" max={maxQuantity} type="number" value={amountToBuy} disabled/>
                                <Button onClick={e => buyMore()}>+</Button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            );
        }else{
            return <div></div>
        }
    }

    return (
        <div>
            {handleDisplay()}
        </div>
    )
}
 
export default Portfolio;
