import React, { useState, useEffect } from 'react';


const Portfolio = props => {
    const handleDisplay = () => {
        if(props.searchMode){
            return (
                <table className="table table-bordered">
                    <caption className="text-center">{props.stockName}</caption>
                    <tbody>
                        <tr>
                            <td>Price</td>
                            <td>{props.price}</td>
                        </tr>
                    </tbody>
                </table>
            );
        }else{
            return <div></div>
        }
    }

    return (
        <div>
            {handleDisplay()}
        </div>
    )
}
 
export default Portfolio;
