import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import axios from 'axios';

const SearchBar = props => {
    const [searchItem,setSearchItem] = useState('');
    const [searching,setSearching] = useState(0);

    useEffect(() => {
        let url = 'https://www.alphavantage.co/query?';
        let func = 'SYMBOL_SEARCH';
        let keywords = searchItem;
        let apiKey = process.env.REACT_APP_ALPHA_VANTAGE_API_KEY;
        console.log('use effect gets called')
        axios.get(`${url}function=${func}&keywords=${keywords}&apikey=${apiKey}`)
        .then(json => {
            console.log(json.data);
        });
        //     return json.data['bestMatches'];
        // })
        // .then(data => {
        //     setPrices(Object.entries(data)[0][1]['4. close'])
        // });
    },[searching])

    const handleChange = e => {
        let searchTerm = e.target.value;
        setSearchItem(searchTerm);
    }

    const handleSearch = () => {
        console.log('click')
        setSearching(searching++);
        props.handleSearch(searchItem);
    }

    return (
        <div>
            <input type="text" onChange={e => handleChange(e)} value={searchItem} /> 
            <Button variant="danger" onClick={e => handleSearch()}>Search</Button>
        </div>
    );
}
 
export default SearchBar;
